INSERT INTO `categories` VALUES (1,'Vì Trẻ Em','Giúp đỡ trẻ em vùng cao, trẻ em nghèo, mồ côi, có hoàn cảnh khó khăn','Active'),(2,'Người Già, Người Khuyết Tật','Giúp đỡ người già neo đơn, người vô gia cư, không nơi nương tựa','Active'),(3,'Bệnh Hiểm Nghèo','Giúp đỡ trẻ em, người mắc bệnh hiểm nghèo','Active'),(4,'Hoàn Cảnh Khó Khăn','Giúp đỡ gia đình - hoàn cảnh khó khăn được hòa nhập cuộc sống','Active'),(5,'Hỗ Trợ Giáo Dục','Xây dựng trường học - Tủ sách yêu thương tiếp sức đến trường cho trẻ em nghèo','Active'),(6,'Đầu Tư Cơ Sở Vật Chất','Giúp đỡ xây dựng cầu đường, cải tạo nước sạch cho những vùng sâu vùng xa khó khăn','Active'),(7,'Cứu Trợ Động Vật','Cứu trợ động vật, chó mèo bị bỏ rơi hoặc bị thương cần được chữa trị và chăm sóc phục hồi','Active'),(8,'Bảo Vệ Môi Trường','Bảo vệ môi trường - Trồng rừng, cải tạo môi trường xanh, nước sạch ','Active'),(9,'123sdfsf','q3wrwetertgert','Inactive'),(10,'556756765','3467568769','Inactive'),(11,'ưqrfsedgd','sdgfhfgdjj','Inactive');
