INSERT INTO `foundation` VALUES (1,'Sức Mạnh 2000 - Ánh sáng núi rừng','Quỹ Sức mạnh 2000 thuộc hệ sinh thái Nuôi Em, được thành lập để nâng cao chất lượng Cơ sở vật chất cho các em nhỏ khó khăn vùng cao.\r\nMỗi người 2.000 đồng/ngày, chúng tôi đã xây dựng trong 10 năm qua','sucmanh2000@gmail.com','Active'),(2,'Quỹ Hy Vọng - Hope Foundation','Ra đời năm 2017, Quỹ Hy Vọng là một quỹ xã hội - từ thiện hoạt động vì cộng đồng, không vì lợi nhuận, được vận hành bởi Báo điện tử VnExpress và Công ty cổ phần FPT. Quỹ Hy vọng theo đuổi hai mục tiêu: hỗ trợ các hoàn cảnh khó khăn và tạo động lực phát triển.','hopefoundation@gmail.com','Active'),(3,'Quỹ Từ Thiện Hoa Chia Sẻ','Quỹ xã hội – từ thiện được thành lập tự nguyện, không vì lợi nhuận, trên nguyên tắc tự tạo vốn. Quỹ hoạt động nhằm mục đích xã hội – từ thiện, ưu tiên giúp đỡ các gia đình khó khăn, có công với cách mạng và hỗ trợ, khuyến khích phát triển văn hóa, giáo dục, y tế, thể dục thể thao, khoa học và các mục đích phát triển cộng đồng phù hợp quy định của pháp luật.','hoachiase@gmail.com','Active'),(4,'VinaCapital Foundation','Tổ chức VinaCapital Foundation (VCF) hoạt động với sứ mệnh trao quyền cho trẻ em và phụ nữ Việt Nam thông qua các chương trình y tế và giáo dục.','vinacapital@gmail.com','Active'),(5,'Quỹ Vì Tầm Vóc Việt','Quỹ Vì Tầm Vóc Việt là tổ chức phi lợi nhuận được thành lập với sứ mệnh đóng góp cho các Mục tiêu Phát triển Bền vững của Liên Hợp Quốc.','tamvocviet@gmail.com','Active'),(6,'Saigon Children’s Charity','Saigon Children’s Charity (saigonchildren) hoạt động với sứ mệnh xóa bỏ rào cản đến với giáo dục cho trẻ em thiệt thòi trên khắp Việt Nam.','saigonchildren@gmail.com','Active'),(7,'SCDI – Mỗi Ngày Một Quả Trứng','Quỹ Mỗi Ngày Một Quả Trứng (MNMQT) là sáng kiến gây quỹ của Trung tâm Hỗ trợ Sáng kiến Phát triển Cộng đồng (SCDI) nhằm cứu trợ khẩn cấp, nâng cao khả năng hồi phục, thích ứng và cắt đứt vòng xoáy đói nghèo cho những người khó khăn nhất.','scdi@gmail.com','Active'),(8,'Thiện Nhân & Friends - Quỹ Phòng chống Thương vong Châu Á','Chương trình “Thiện Nhân và những người bạn” phẫu thuật tái tạo bộ phận sinh dục cho trẻ em thuộc Quỹ Phòng chống Thương vong Châu Á.','thiennhanfriends@gmail.com','Active'),(9,'Trung tâm Tương Lai','Trung tâm Tương Lai là tổ chức chuyên hoạt động để hỗ trợ giáo dục và bảo vệ trẻ em và thanh thiếu niên.','tuonglaicenter@gmail.com','Active'),(10,'Quỹ Trăng Khuyết','Trăng Khuyết là tổ chức từ thiện nhân đạo, hoạt động độc lập, phi tôn giáo và phi lợi nhuận trên toàn lãnh thổ Việt Nam, nhằm kết nối cộng đồng và thực hiện các hoạt động cứu trợ trực tiếp liên quan đến: bữa ăn, chỗ ở, chăm sóc sức khỏe - tinh thần cho các cụ già không nơi nương tựa và trẻ em bị bỏ rơi.','quytrangkhuyet@gmail.com','Active'),(11,'Operation Smile','Operation Smile mang lại nụ cười và thay đổi cuộc sống của những trẻ em bị dị tật bẩm sinh trên khuôn mặt ở Việt Nam và khắp thế giới.','operationsmile@gmail.com','Active'),(12,'Viện Nghiên cứu Quản lý Phát triển bền vững (MSD)','Là một tổ chức phi chính phủ Việt Nam, MSD nỗ lực hành động thúc đẩy việc thực hiện quyền của các nhóm cộng đồng bị lề hoá, dễ bị tổn thương. MSD cũng là tổ chức đáp ứng nhu cầu và bảo vệ các đối tượng có hoàn cảnh khó khăn, thông qua các dự án và hỗ trợ trẻ em, thanh thiếu niên, phụ nữ, người vô gia cư, người nhập cư và người khuyết tật tại Việt Nam.','msd@gmail.com','Active'),(15,'new foundati1','new description for new foundation','333333@gmail.com','Inactive'),(16,'12423','123123','12323@mail','Inactive'),(17,'1242355','123123','12323@mail','Inactive');
